import { useEffect, useState } from 'react';
import Board from './Board.js';
import { createGlobalStyle } from 'styled-components';

// import test from './test'

const GlobalStyle = createGlobalStyle`
    :root {
      --black: black;
      --white: #bcbbbf;
      --purple: #270973;
      --dark-grey: #111;
    }

    body {
      background-color: var(--dark-grey);
      color: var(--white);
    }
`

function App() {

  const [gameState, setGameState] = useState({
    pieces: '',
    colors: '',
    whites_turn: true,
    toIndex: null,
    fromIndex: null
  })

  useEffect(() => {
    fetch('http://127.0.0.1:5555/moves')
      .then(r => r.json())
      .then(data => {setGameState(data.slice(-1)[0])})
  }, []);

  async function handleMove(fromIndex, toIndex) {
    const configObj = {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json",
      },
      body: JSON.stringify({fromIndex, toIndex,}),
    };

    const newGameState = await fetch('http://127.0.0.1:5555/moves', configObj)
      .then(r => r.json());
    setGameState(newGameState)

    const {pieces} = gameState;
    const {pieces: newPieces} = newGameState;

    const validMove = !(pieces == newPieces);
    return validMove
  };

  return (
    <>
      <GlobalStyle />
      <h1>Chess by Kyle</h1>
      <Board 
        gameState={gameState}
        handleMove={handleMove}
      />
    </>
  );
}

export default App;
