import { createGlobalStyle } from "styled-components";

const MovesStyle = createGlobalStyle`
    .moves-container {
        height: 40vw;
        width: 20vw;
        background-color: var(--light-grey);
        margin-left: 5vw;
        border-radius: 5%;
    }

    h2 {
        text-align: center;
    }

    .li {
        list-style-type: none;
    }
`

const posToCart = (position) => {
    const row = Math.floor(position / 8);
    const col = position % 8;
    return `${row}${col}`
};``

function Moves({ moves }) {

    const moveItems = moves.slice(1).map(move => {
        return <li key={move[0]}>
                   {posToCart(move[1])} {posToCart(move[2])}
               </li>;
    });

    return (
        <>
            <MovesStyle />
            <div className='moves-container'>
                <h2>Moves</h2>
                <ol>{moveItems}</ol>
            </div>
        </>
    );
}

export default Moves;
