import { createGlobalStyle } from "styled-components";

// ♟ black pawn
// ♙ white pawn

const piece_dictionary_text = {
    'BB': '♝',
    'BH': '♞',
    'BK': '♚',
    'BP': '',
    'BQ': '♛',
    'BR': '♜',
    'WB': '♗',
    'WH': '♘',
    'WK': '♔',
    'WP': '',
    'WQ': '♕',
    'WR': '♖', 
}

const MovesStyle = createGlobalStyle`
    .moves-container {
        height: 40vw;
        width: 20vw;
        background-color: var(--light-grey);
        margin-left: 5vw;
        border-radius: 5%;
    }

    .move-div-container {
        display: inline-block;
        margin: auto;
    }

    h2 {
        text-align: center;
    }

    li img {
        height: 2.5vh;
    }
`

const posToCoord = (position) => {
    const row_index = (position % 8);
    const row = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'][row_index]
    const col = 8 - Math.floor(position / 8);
    return `${row}${col}`
};

function Moves({ moves }) {

    const moveStrings = moves.slice(1).map(move => {
        const color = move['values']['color'];
        const piece = move['values']['piece'];
        const capture = move['values']['capture'];
        const toCoord = posToCoord(move['values'['toIndex']])

        if (move['values']['castle'] == 'short') return 'O-O';
        if (move['values']['castle'] == 'long') return 'O-O-O';

        let pieceText;
        pieceText = piece_dictionary_text[`${color}${piece}`]

        return `${pieceText}${capture ? 'x' : null}${toCoord}`
    });

    console.log(moveStrings)

    const moveDivs = moves.slice(1).map(move => {
        const color = move['values']['color']
        const piece = move['values']['piece']

        if (move['values']['castle'] == 'short') {
            return <li key={move['index']}>
            <div className='move-container'>
                O-O
             </div> 
        </li>;
        };

        if (move['values']['castle'] == 'long') {
            return <li key={move['index']}>
            <div className='move-container'>
                O-O-O
             </div> 
        </li>;
        };

        let piecePng;
        // piecePng = piece_dictionary[`${color}${piece}`]
        if (piece == 'P') piecePng = '';
        return <div key={move['index']} className='move-container'>
                 <img src={piecePng} />
                 {move['values']['capture'] ? 'x' : null}
                 {posToCoord(move['values']['toIndex'])}
               </div> 
    });

    const moveDivsByTwo = [];
    if (moveDivs.length % 2 == 0) {
        while (moveDivs.length > 1) moveDivsByTwo.push(moveDivs.splice(0,2));
        if (moveDivs.length == 1) moveDivsByTwo.push([moveDivs[0]]);
    };
    console.log(moveDivsByTwo)

    const moveLis = moveDivsByTwo.map(movePair => {
        return <li><div className='move-div-container'>{movePair[0]}{movePair[1]}</div></li>
    });

    return (
        <>
            <MovesStyle />
            <div className='moves-container'>
                <h2>Moves</h2>
                <ol>{moveLis}</ol>
            </div>
        </>
    );
}

export default Moves;
