import { useState } from 'react';
import { createGlobalStyle } from 'styled-components';
import Draggable from 'react-draggable';

import piece_dictionary from './PiecePngs';

const BoardStyle = createGlobalStyle`

    .board-container {
        height: 40vw;
        width: 40vw;
        display: grid;
        grid-template-columns: repeat(8, 1fr);
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
    }

    .light-square, .dark-square, .trans-square {
        height: 5vw;
        width: 5vw;
        display: flex;
    }

    .trans-square {
      border: 1px solid black;
      opacity: 0;
      height: 100%;
      width: 100%;
      z-index: 1;
    }

    .light-square {
        background-color: var(--white);
    }

    .dark-square {
        background-color: var(--purple);
    }

    img {
      z-index: 0;
    }

`

function Board({ gameState }) {

    console.log(window.innerWidth / 10)

    const [state, setState] = useState({
      activeDrags: 0,
      deltaPosition: {x: 0, y: 0}
  })

    // const state = {
    //     activeDrags: 0,
    //     deltaPosition: {x: 0, y: 0}
    // }

    const handleDrag = (e, ui) => {
        const {x, y} = state.deltaPosition;
        setState(state => {
          state.deltaPosition = {
            x: x + ui.deltaX,
            y: y + ui.deltaY            
          }
        });
        // state.deltaPosition = {
        //   x: x + ui.deltaX,
        //   y: y + ui.deltaY
        // };
        console.log(state.deltaPosition)
    };
    const onStart = (e) => {
        setState(state => ++state.activeDrag)
        // ++state.activeDrags
    };
    const onStop = (e) => {
      setState(state => --state.activeDrag)
      // --state.activeDrags;
    };

    const pieces_array = gameState.pieces.split('')
    const colors_array = gameState.colors.split('')

    if (pieces_array.length == 0 || colors_array.length == 0) {
        return <></>
    };

    const boardDivs = [];

    let row = 0;
    for (let i = 0; i < 64; i++) {
        // set color
        let shade;
        if ((i % 2 == 0 && row % 2 == 0) || (i % 2 == 1 && row % 2 == 1)) {
            shade = 'light'
        } else shade = 'dark';

        // set piece
        let piecePng;
        if (colors_array[i] == 'E') {
            piecePng = ''
        } else {
            const color = colors_array[i];
            const piece = pieces_array[i];
            piecePng = piece_dictionary[`${color}${piece}`]
        };

        const square_id = `square${i}`;
        boardDivs.push(
          <div 
            className={`${shade}-square`} 
            key={i}
          >
            <Draggable 
              onDrag={handleDrag}
              onStart={onStart}
              onStop={onStop}
              bounds='.board-container'
            >
              <img draggable='false' src={piecePng} />
            </Draggable>
            <div 
              id={square_id} 
              className='trans-square' 
              onMouseMove={(e) => console.log(e.target.id)}
            ></div>
          </div>
        )

        if ((i + 1) % 8 == 0) {
            row += 1
        };

    }

    return (
        <>
          <BoardStyle />
          <div className='board-container'>
            {boardDivs}
          </div>
          <h2>
            {/* {state.deltaPosition} */}
          </h2>
        </>
    );
}

export default Board;
