import { createGlobalStyle } from "styled-components";

const MovesStyle = createGlobalStyle`
    .moves-container {
        height: 40vw;
        width: 20vw;
        background-color: var(--light-grey);
        margin-left: 5vw;
        border-radius: 5%;
    }

    h2 {
        text-align: center;
    }

    .li {
        list-style-type: none;
    }
`

const posToCoord = (position) => {
    const row_index = (position % 8);
    const row = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'][row_index]
    const col = 8 - Math.floor(position / 8);
    return `${row}${col}`
};

function Moves({ moves }) {

    const moveItems = moves.slice(1).map(move => {
        return <li key={move['index']}>
                   {posToCoord(move['values']['toIndex'])}
               </li>;
    });

    return (
        <>
            <MovesStyle />
            <div className='moves-container'>
                <h2>Moves</h2>
                <ol>{moveItems}</ol>
            </div>
        </>
    );
}

export default Moves;
